document.addEventListener('DOMContentLoaded', function () {
	
	function templating (data) {
		let html = ''
	   
	    $.each(data, function(index, item){
	        html += '<div class="articles__list-item col-12 col-md-4 post">\
			        	<figure class="post__thumb">\
							<a href="/single.html">\
								<img src="img/dist/portfolio/thumb-second.png" srcset="img/dist/Blog.jpg"\
									class="lazy example-img"\
									alt="blog img">\
							</a>\
						</figure>\
						<div class="post__content">\
							<h3 class="post__title"><a href="/single.html">'+item+'Title</a></h3>\
							<p class="post__excerpt">Lorem ipsum, dolor sit amet consectetur\
							adipisicing elit. Vitae, quidem.</p>\
						</div>\
					</div>'
	    })

	    return html
	}

	$('#pagination-container').pagination({
	    dataSource: [1, 2, 3, 4, 5, 6,8,9,10],
	    pageSize: 3,
	    callback: function(data, pagination) {
	        var html = templating(data)
        	$('#data-container').html(html)	        
	    }
	})
})